INSERT INTO bb1_bbcodes VALUES (1,'b','<b>\\1</b>','[B]Text[/B]','Hiermit kann ein Text fett dargestellt werden.',1,1,'','','',0),(2,'i','<i>\\1</i>','[I]Text[/I]','Hiermit kann ein Text kursiv dargestellt werden.',1,1,'','','',0),(3,'email','<a href=\"mailto:\\1\">\\1</a>','[EMAIL]Name@email.de[/EMAIL]','Hiermit kann eine Email-Adresse als klickbarer Link dargestellt werden.',1,1,'','','',0),(4,'email','<a href=\"mailto:\\2\">\\3</a>','[EMAIL=name@email.de]Name[/EMAIL]','Hiermit kann eine Email-Adresse als klickbarer Link dargestellt werden. (Der Linkname kann jedoch selbst bestimmt werden.)',2,1,'','','',0),(5,'size','<span style=\"font-size: \\2px;\">\\3</span>','[SIZE=3]Text[/SIZE]','Hiermit kann ein Text mit der gewählten Größe dargestellt werden.',2,3,'[1-3]?[0-9]{1}','','',0),(6,'quote','<table align=\"center\" width=\"98%\" cellpadding=\"4\" cellspacing=\"1\" class=\"tableinborder\">\r\n <tr>\r\n  <td align=\"left\" class=\"tablecat\"><span class=\"smallfont\"><b>{$lang->items[\'LANG_GLOBAL_QUOTE\']}</b></span></td>\r\n </tr>\r\n <tr class=\"normalfont\">\r\n  <td class=\"inposttable\" align=\"left\">\\1</td>\r\n </tr>\r\n</table>','[QUOTE]Dieser Text wird zitiert.[/QUOTE]','Hiermit kann ein Zitat eingefügt werden.',1,10,'','','',1),(7,'u','<u>\\1</u>','[U]Text[/U]','Hiermit kann ein Text unterstrichen dargestellt werden.',1,1,'','','',0),(8,'color','<span style=\"color: \\2;\">\\3</span>','[COLOR=#0000FF]Text[/COLOR]','Hiermit kann ein Text farbig dargestellt werden.',2,3,'[0-9a-z#]*','','',0),(9,'font','<span style=\"font-family: \\2;\">\\3</span>','[FONT=ARIAL]Text[/FONT]','Hiermit kann ein Text mit der gewählten Schriftart dargestellt werden.',2,3,'[^\\"\';}]*','','',0),(12,'align','<div align=\"\\2\">\\3&nbsp;</div>','[ALIGN=right]rechtsbündiger Text[/ALIGN]','Hiermit können Elemente wie Text, Grafiken usw. ausgerichtet werden. Mögliche Werte sind \"right\", \"left\", \"center\" und \"justify\".',2,1,'','','',0),(11,'center','<div align=\"center\">\\1</div>','[CENTER]zentrierter Text[/CENTER]','Hiermit kann ein Text zentriert ausgerichtet werden.',1,1,'','','',0);
